const http=require("http"),https=require("https"),urls=process.argv.slice(2);0===urls.length&&(console.error("Please provide at least one URL as a command-line argument."),process.exit(1));const port=process.env.PORT||1e4,server=http.createServer((e,t)=>{t.end()});function makeRequest(e){https.get(e,t=>{console.log(`Request sent to ${e}. Status Code: ${t.statusCode}`)}).on("error",t=>{console.error(`Error for ${e}: ${t.message}`)})}function scheduleNextRequest(e){let t=6e4*Math.floor(3*Math.random()+2);console.log(`Next request to ${e} in ${t/1e3/60} minutes.`),setTimeout(()=>{makeRequest(e),scheduleNextRequest(e)},t)}server.listen(port,()=>{console.log(`Server is running on port ${port}`)}),urls.forEach(e=>scheduleNextRequest(e));